import React, {Component} from 'react';
import axios from 'axios';

export default class ProdList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            username: this.props.location.user
        }
    }

    componentDidMount() {
        const newProduct = {
            username: this.state.username,
            name: '',
            Price: 0,
            Quantity: 0
        };

        axios.post('http://localhost:4000/login/customer/products',newProduct)
             .then(response => {
                 this.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    cancelProduct = (name,count1) => {
        const upProduct = {
            vendorname: this.state.username,
            productname: name,
            count: count1,
            status: 'canceled',
        }
        axios.put('http://localhost:4000/updateorderdb',upProduct)
            .then(res => console.log(res.data));

        axios.put('http://localhost:4000/updateorder',upProduct)
            .then(res => console.log(res.data));
        alert("canceled");
        this.props.history.push({
			pathname:'/login/vendor',
			user: this.state.username
		});
    }

    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Vendor name</th>
                            <th>Name</th>
                            <th>Quantity</th>
                            <th>Quantity Left</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.products.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.vendorname}</td>
                                    <td>{currentUser.productname}</td>
                                    <td>{currentUser.quantity}</td>
                                    <td>{currentUser.count}</td>
                                    <td>{currentUser.status}</td>
                                    <td><button type="button" onClick={() => this.editProduct(currentUser.name,currentUser.count)}>Edit</button></td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
