import React, {Component} from 'react';
import axios from 'axios';

export default class ProdList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            username: this.props.location.user,
            name: this.props.location.name
        }
    }

    searchProducts = () => {
		this.props.history.push({
			pathname:'/login/customer/search-product',
			user: this.state.username
		});
	}

	viewProduct = () => {
		this.props.history.push({
			pathname:'/login/customer/products',
			user: this.state.username
		});
    }

    orderProduct = (username,quantity1,count1) => {
        this.props.history.push({
            pathname:'/login/customer/search-product/result/order',
            user: this.state.username,
            name: this.state.name,
            vendorname: username,
            quantity: quantity1,
            count: count1
        });
    }

    viewProductSrtPrc = () => {
        let sortedProductsAsc;
          sortedProductsAsc= this.state.products.sort((a,b)=>{
             return parseInt(a.price)  - parseInt(b.price);
          })

          this.setState({
              products:sortedProductsAsc
          })
    }

    viewProductSrtQuan = () => {
        let sortedProductsAsc;
          sortedProductsAsc= this.state.products.sort((a,b)=>{
             return parseInt(a.count)  - parseInt(b.count);
          })

          this.setState({
              products:sortedProductsAsc
          })
    }

    viewProductSrtRat = () => {
        let sortedProductsAsc;
          sortedProductsAsc= this.state.products.sort((a,b)=>{
             return parseInt(a.vrating)  - parseInt(b.vrating);
          })

          this.setState({
              products:sortedProductsAsc
          })
    }

    componentDidMount() {
        const newProduct = {
            username: '',
            name: this.state.name,
            Price: 0,
            Quantity: 0
        };
        axios.post('http://localhost:4000/login/customer/search-product/result',newProduct)
             .then(response => {
                 this.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <div className="collapse navbar-collapse">
                        <ul className="navbar-nav mr-auto">
                        <li className="navbar-item">
                            <button type="button" onClick={this.searchProducts}>Search Products</button>
                        </li>
                        <li className="navbar-item">
                            <button type="button" onClick={this.viewProduct}>View Products</button>
                        </li>
                        <li>
                            <button type="button" onClick={this.viewProductSrtPrc}>Sort by Price</button>
                        </li>
                        <li>
                            <button type="button" onClick={this.viewProductSrtQuan}>Sort by Items left</button>
                        </li>
                        <li>
                            <button type="button" onClick={this.viewProductSrtRat}>Sort by Rating of customer</button>
                        </li>
                        </ul>
                    </div>
                    <div>
                        {this.state.username}
                    </div>
                    </nav>
                    <br/>
                </div>
                <div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Username</th>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Quantity Left to order</th>
                                <th>Status of Product</th>
                                <th>Rating</th>
                                <th>Vendor Rating</th>
                                <th>Review</th>
                            </tr>
                        </thead>
                        <tbody>
                        { 
                            this.state.products.map((currentUser, i) => {
                                return (
                                    <tr>
                                        <td>{currentUser.username}</td>
                                        <td>{currentUser.name}</td>
                                        <td>{currentUser.price}</td>
                                        <td>{currentUser.quantity}</td>
                                        <td>{currentUser.count}</td>
                                        <td>{currentUser.status}</td>
                                        <td>{currentUser.rating}</td>
                                        <td>{currentUser.vrating}</td>
                                        <td>{currentUser.review}</td>
                                        <td><button type="button" onClick={() => this.orderProduct(currentUser.username,currentUser.quantity,currentUser.count)}>Order</button></td>
                                    </tr>
                                )
                            })
                        }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
